mapper.delete:
delete from TB_ALARM_STATISTICS
where 
	PROVINCEID = #{PROVINCEID}
and
    CITYID = #{CITYID}

mapper.select:
select sum(CRITICALALARMS) as CRITICALALARMS, sum(MAJORALARMS) as MAJORALARMS, sum(MINORALARMS) as MINORALARMS, sum(NORMALALARMS) as NORMALALARMS, sum(COMMUNICATIONSALARMS) as COMMUNICATIONSALARMS, sum(SOFTWAREALARMS) as SOFTWAREALARMS, sum(ENVIRONMENTALARMS) as ENVIRONMENTALARMS, sum(QOSALARMS) as QOSALARMS, sum(EQUIPMENTALARMS) as EQUIPMENTALARMS, sum(NMSALARMS) as NMSALARMS from TB_ALARM_STATISTICS where PROVINCEID = #{PROVINCEID}
and CITYID = #{CITYID}
and OPDATE >= #{STARTTIME}
and OPDATE <= #{ENDTIME}

select sum(CLEARCOUNT) as CLEARCOUNT, sum(CRITICALALARMLONGCOUNT) as CRITICALALARMLONGCOUNT, sum(MAJORALARMLONGCOUNT) as MAJORALARMLONGCOUNT, sum(CRITICALALARMRESOLVETIME) as CRITICALALARMRESOLVETIME, sum(MAJORALARMRESOLVETIME) as MAJORALARMRESOLVETIME, OPDATE from TB_ALARM_STATISTICS where PROVINCEID = #{PROVINCEID}
and CITYID = #{CITYID}
and OPDATE >= #{STARTTIME}
and OPDATE <= #{ENDTIME}
group by OPDATE

select sum(a.CRITICALALARMS+a.MAJORALARMS+a.MINORALARMS+a.NORMALALARMS) as TOTALALARMS, sum(a.CRITICALALARMS) as CRITICALALARMS, sum(a.MAJORALARMS) as MAJORALARMS, sum(a.MINORALARMS) as MINORALARMS, sum(a.NORMALALARMS) as NORMALALARMS, sum(a.COMMUNICATIONSALARMS) as COMMUNICATIONSALARMS, sum(a.SOFTWAREALARMS) as SOFTWAREALARMS, sum(a.ENVIRONMENTALARMS) as ENVIRONMENTALARMS, sum(a.QOSALARMS) as QOSALARMS, sum(a.EQUIPMENTALARMS) as EQUIPMENTALARMS, sum(a.NMSALARMS) as NMSALARMS, a.CITYID, c.VALUE as CITYNAME from TB_ALARM_STATISTICS a left join TB_CITY_INFO c on a.CITYID = c.CITYID where a.PROVINCEID = #{PROVINCEID}
and a.OPDATE >= #{STARTTIME}
and a.OPDATE <= #{ENDTIME}
group by a.CITYID
