mapper.select:
		select 			
			count(CUR_ALARM_ID) as COUNT
		from 
			TB_CUR_ALARM 
		where 
            TASKID = #{TASKID}
        and
            PROVINCEID = #{PROVINCEID}
        and
            CITYID = #{CITYID}
        and
            SYSTEMID = #{SYSTEMID}    
        and
            DEVID = #{DEVID} 
        and 
            ALARMID = #{ALARMID}

mapper.delete:
delete from TB_CUR_ALARM
where 
	PROVINCEID = #{PROVINCEID}
and
    CITYID = #{CITYID}
