mapper.select:
		select 			
			count(PM_STATISTICS_ID) as COUNT
		from 
			TB_PM_STATISTICS 
		where 
            TASKID = #{TASKID}
        and
            PROVINCEID = #{PROVINCEID}
        and
            CITYID = #{CITYID}
        and
            SYSTEMID = #{SYSTEMID}    
        and
            DEVID = #{DEVID} 
        and
            OPDATE = #{OPDATE}

select sum(GROUPCALLS) as GROUPCALLS, sum(GROUPCALLDURATION) as GROUPCALLDURATION, sum(PRIVATECALLS) as PRIVATECALLS, sum(PRIVATECALLDURATION) as PRIVATECALLDURATION, sum(EMERGENCYCALLS) as EMERGENCYCALLS, sum(EMERGENCYCALLDURATION) as EMERGENCYCALLDURATION, sum(PHONECALLS) as PHONECALLS, sum(PHONECALLDURATION) as PHONECALLDURATION , sum(GROUPCALLS+PRIVATECALLS+EMERGENCYCALLS+PHONECALLS) as TOTALCALLTIMES, sum(GROUPCALLDURATION+PRIVATECALLDURATION+EMERGENCYCALLDURATION+PHONECALLDURATION) as TOTALCALLENGTH, sum(FAILEDGROUPCALLS) as FAILEDGROUPCALLS, sum(FAILEDPRIVATECALLS) as FAILEDPRIVATECALLS, sum(FAILEDEMERGENCYCALLS) as FAILEDEMERGENCYCALLS, sum(FAILEDPHONECALLS) as FAILEDPHONECALLS from TB_PM_STATISTICS where PROVINCEID = #{PROVINCEID}
and CITYID = #{CITYID}
and TO_DAYS(OPDATE) >= TO_DAYS(#{BEGINDATE})
and TO_DAYS(OPDATE) <= TO_DAYS(#{ENDDATE})

mapper.delete:
delete from TB_PM_STATISTICS
where 
	PROVINCEID = #{PROVINCEID}
and
    CITYID = #{CITYID}
