mapper.delete:
delete from TB_TOPO_INFO
where 
    TOPO_INFO_ID = TOPO_INFO_ID
and 
    (

    (	TASKID = #{i.TASKID}
	and
    	PROVINCEID = #{i.PROVINCEID}
	and
    	CITYID = #{i.CITYID}
	and 
    	DEVID = #{i.DEVID} 
    )

    )

mapper.select:
select TASKID, fn_provinceNameById(PROVINCEID) AS PROVINCENAME, fn_cityNameByCityId(CITYID) AS CITYNAME, DEVTYPE, DEVNAME, MANUFACTURER, SYSTEMID, DEVID, DEVSTATE, LONGITUDE, LATITUDE, ALTITUDE, BSRLIST, ANTALTITUDE, ANTNUM, ANTTYPE, DIVERSITY, BANDWIDTH, LINKTYPE, ROOMPOWER, ROOMTYPE, ROOMSTATUS, COVERAREA, DISTRICT, TSADDRESS, INTIME, TOPO_INFO_ID from TB_TOPO_INFO where DEVID = #{DEVID}
and CITYID = #{CITYID}
and PROVINCEID = #{PROVINCEID}
LIMIT 1

mapper.delete:
delete from TB_TOPO_INFO
where 
	PROVINCEID = #{PROVINCEID}
and
    CITYID = #{CITYID}
